import React, { Component } from "react";
import { connect } from "react-redux";
import uuidv1 from "uuid";
// import { addArticle, deleteArticle } from "../actions/index";
import { addArticle } from "../actions/articles";

const mapDispatchToProps = dispatch => {
  return {
    addArticle: article => dispatch(addArticle(article))
  };
};
class ConnectedForm extends Component {
  constructor() {
    super();
    this.state = {
      articles: ""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({ [event.target.id]: event.target.value });
  }
  handleSubmit(event) {
    event.preventDefault();
    const { articles } = this.state;
    const id = uuidv1();
    this.props.addArticle({ articles, id });
    this.setState({ articles: "" });
  }
  render() {
    const { articles } = this.state;
    return (
      <form onSubmit={this.handleSubmit}>
        <div className="form-group">
          <label htmlFor="articles">Novo Artigo</label>
          <input
            type="text"
            className="form-control"
            id="articles"
            value={articles}
            onChange={this.handleChange}
          />
        </div>
        <button type="submit" className="btn btn-success btn-lg">
          Novo Artigo
        </button>
      </form>
    );
  }
}
const FormArticles = connect(null, mapDispatchToProps)(ConnectedForm);
export default FormArticles;
