// src/js/components/List.js
import React, { Component } from "react";
import { connect } from "react-redux";
import { deleteArticle, fetchArticles } from "../actions/index";

const mapStateToProps = state => {
  return { articles: state.articles.articles };
};

const mapDispatchToProps = dispatch => {
  return {
    deleteArticle: article => dispatch(deleteArticle(article)),
    fetchArticles: location => dispatch(fetchArticles(location)),
  };
};

class ConnectedList extends Component{
  constructor(){
    super();
    this.deleteThisArticle = this.deleteThisArticle.bind(this);
  }

  deleteThisArticle(article){
    console.log(article);
    this.props.deleteArticle(article);
  }

  componentDidMount(){
    //chamada inicial para ir buscar os artigos
    console.log(this.props.location.pathname);
    this.props.fetchArticles({ type: "FETCH_ARTICLES" });
    console.log(this.props.fetchArticles({ type: "FETCH_ARTICLES", location: this.props.location.pathname,  }));
  }

  render() {
    const articles = this.props.articles;
    return (
      <div>
      <h1>Lista de utilizadores</h1>
      <ul className="list-group list-group-flush">
        {articles.map((el, index) => (
          <li className="list-group-item" key={index}>
            {el.name}
            <button className="btn btn-danger btn-lg" onClick={() => this.deleteThisArticle(el)}>Delete</button>
          </li>
        ))}
      </ul>

      </div>

    )
  }

}

