import * as mySagaUser from './users';
import * as mySagaComment from './comments';
import * as mySagaArticle from './articles';

const sagas = {
  ...mySagaUser,
  ...mySagaArticle,
  ...mySagaComment,
}

export default function registerWithMiddleware(middleware: { run: Function }) {
  for (let name in sagas) {
    middleware.run(sagas[name]);
  }
}
