import * as userSagas from 'users';
import * as commentSagas from 'comments';
// import * as articleSagas from 'articles';

const sagas = {
  ...userSagas,
  ...commentSagas,
  // ...articleSagas,
}

export default function registerWithMiddleware(middleware: { run: Function }) {
  for (let name in sagas) {
    middleware.run(sagas[name]);
  }
}
