import React, { Component } from "react";
import { connect } from "react-redux";
import { deleteArticle } from "../actions/articles";
import { fetchArticle } from "../actions/articleData";

const mapStateToProps = state => {

  return { articleInfo: state.articleInfo };
};

const mapDispatchToProps = dispatch => {
  return {
    // deleteArticle: article => dispatch(deleteArticle(article)),
    fetchArticle: article => dispatch(fetchArticle(article)),
  };
};

class ConnectedList extends Component{

  constructor(){
    super();
    // this.deleteThisArticle = this.deleteThisArticle.bind(this);
  }

  // deleteThisArticle(article){
  //   console.log(article);
  //   this.props.deleteArticle(article);
  // }

  componentDidMount(){
    //chamada inicial para ir buscar o artigo do ID
    const id = this.props.match.params.id;

    this.props.fetchArticle({ type: "FETCH_USER", article: id });

  }

  render(){
    const article = this.props.articleInfo.articleInfo;
    const comments = article.comments;
    console.log(article);
    if(article.comments)
    {
    return(
      <div>
        <div>Titulo: {article.title}</div>
        <div>Descrição: {article.description}</div>
        <div>
        <ul className="list-group list-group-flush">
          {comments.map((el, index) => (
            <li className="list-group-item" key={index}>
              {el.commentText}
              <button className="btn btn-danger btn-lg">Eliminar o comentário</button>
            </li>
          ))}
        </ul>
        </div>
        <div>
          <button className="btn btn-danger btn-lg">Eliminar o artigo</button>
        </div>
      </div>
    )
  } else {
    return(
      <div>Loading....</div>
    )
  }
  }
}

const List = connect(mapStateToProps, mapDispatchToProps)(ConnectedList);
export default List;
