import React, { Component } from "react";
import { connect } from "react-redux";
// import uuidv1 from "uuid";
import { addComment } from "../actions/comments";
import { fetchArticle } from "../actions/articleData";

const mapStateToProps = state => {

  return { articleInfo: state.articleInfo };
};

const mapDispatchToProps = dispatch => {
  return {
    addComment: comment => dispatch(addComment(comment)),
    fetchArticle: article => dispatch(fetchArticle(article)),
  };
};
class ConnectedForm extends Component {
  constructor() {
    super();
    console.log('comment', this);
    this.state = {
      commentText: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ [event.target.id]: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    const { commentText } = this.state;

    const id = "";
    this.props.addComment({ commentText });
    this.setState({ commentText: "", article_id: this.props.match.params.id});
  }

  componentDidMount(){
    //chamada inicial para ir buscar o artigo do ID
    console.log('TGIS', this);
    const id = this.props.match.params.id;;

    this.props.fetchArticle({ type: "FETCH_USER", article: id });

  }

  render() {
    const { commentText } = this.state;
    return (
      <form onSubmit={this.handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Comentário</label>
          <input
            type="text"
            className="form-control"
            id="commentText"
            value={commentText}
            onChange={this.handleChange}
          />
        </div>

        <button type="submit" className="btn btn-success btn-lg">
          Novo Comentário
        </button>
      </form>
    );
  }
}
const FormComments = connect(mapStateToProps, mapDispatchToProps)(ConnectedForm);
export default FormComments;
