import React, { Component } from "react";
import { connect } from "react-redux";
import { deleteUser, fetchUsers } from "../actions/users";
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

import UserInfo from "./userData";

const mapStateToProps = state => {
  return { users: state.users.users };
};

const mapDispatchToProps = dispatch => {
  return {
    deleteUser: user => dispatch(deleteUser(user)),
    fetchUsers: () => dispatch(fetchUsers()),
  };
};

class ConnectedList extends Component{
  constructor(){
    super();
    this.deleteThisUser = this.deleteThisUser.bind(this);
  }

  deleteThisUser(user){
    console.log(user);
    this.props.deleteUser(user);
  }

  componentDidMount(){
    //chamada inicial para ir buscar os artigos
    const users = this.props.users;
    if(users == 0){
      this.props.fetchUsers({ type: "FETCH_USERS" });
    }
  }

  render() {
    console.log(this.props.users);
    const users = this.props.users;
    return (
      <Router>
      <div>
      <h1>Lista de utilizadores</h1>
      <ul className="list-group list-group-flush">
        {users.map((el, index) => (
          <li className="list-group-item" key={index}>
            {el.name}
            <button className="btn btn-danger btn-lg" onClick={() => this.deleteThisUser(el)}>Delete</button>
          </li>
        ))}
