import React, { Component } from "react";
import { connect } from "react-redux";
import { deleteUser } from "../actions/users";
import { fetchUser } from "../actions/userData";

const mapStateToProps = state => {
  return { user: state.userInfo };
};

const mapDispatchToProps = dispatch => {
  console.log('ola');
  return {
    // deleteUser: user => dispatch(deleteUser(user)),
    fetchUser: user => dispatch(fetchUser(1)),
  };
};

class ConnectedList extends Component{

  constructor(){
    super();
    this.deleteThisUser = this.deleteThisUser.bind(this);
  }

  deleteThisUser(user){
    console.log(user);
    this.props.deleteUser(user);
  }

  componentDidMount(){
    //chamada inicial para ir buscar os artigos
    const user = this.props.user;
    console.log(user);
    if(user == 0){
      this.props.fetchUser({ type: "FETCH_USER", user: 1});
    }
  }

  render(){
    return(<div><div>Ola</div>
    <div>
      <button className="btn btn-danger btn-lg" onClick={() => this.deleteThisUser(1)}>Delete</button>
    </div>
  </div>)
  }
}

const List = connect(mapStateToProps, mapDispatchToProps)(ConnectedList);
export default List;
