import { call, put, takeLatest } from 'redux-saga/effects';
import { FETCH_ARTICLES, ARTICLES_FETCH_SUCCESS, ARTICLES_FETCH_ERROR } from '../constants/action-types';
import { ENDPOINT } from '../constants/services';
// import { ENDPOINTARTICLE } from '../constants/article-services';
// import { ENDPOINTCOMMENT } from '../constants/comment-services';

function fetchAll(){
  return fetch(ENDPOINT).then(response => response.json(), );
}

function* fetchArticles(location){
  console.log(location.payload.location);
  if(location.payload.location === '/'){
    try{
      const articles = yield call(fetchAll);
      yield put({type: ARTICLES_FETCH_SUCCESS, payload: articles});
      console.log('success try catch');
    } catch(e){
      console.log(e);
      yield put({type: ARTICLES_FETCH_ERROR, message: e.message});
    }
  }
  else if(location.payload.location === '/articles'){
    try{
      const articles = yield call(fetchAllArticles);
      yield put({type: ARTICLES_FETCH_SUCCESS, payload: articles});
      console.log('success try catch');
    } catch(e){
      console.log(e);
      yield put({type: ARTICLES_FETCH_ERROR, message: e.message});
    }
  }
  else if(location.payload.location === '/comments'){
    try{
      const articles = yield call(fetchAllComments);
      yield put({type: ARTICLES_FETCH_SUCCESS, payload: articles});
      console.log('success try catch');
    } catch(e){
      console.log(e);
      yield put({type: ARTICLES_FETCH_ERROR, message: e.message});
    }
  }
}

function* mySaga(){
  console.log('article saga init');
  yield takeLatest(FETCH_ARTICLES, fetchArticles);
}

export default mySaga;
