import { call, put, take } from 'redux-saga/effects';
import { FETCH_USER, USER_FETCH_SUCCESS, USER_FETCH_ERROR } from '../constants/action-types-userData';
// import { ENDPOINT } from '../constants/services';
// import { ENDPOINTUSER } from '../constants/user-services';
// import { ENDPOINTCOMMENT } from '../constants/comment-services';

function fetchDetail(){
  return fetch('http://tdi-api.test:8080/api/user/1').then(response => response.json(), );
}

function* fetchUser(){
  try{
    console.log('is this happening');
    const user = yield call(fetchDetail);
    yield put({type: USER_FETCH_SUCCESS, payload: user});
    console.log('success try catch of one user');
  } catch(e){
    console.log(e);
    yield put({type: USER_FETCH_ERROR, message: e.message});
  }
}

function* mySagaUserDetail(){
  console.log('user saga init of one user');
  yield take(FETCH_USER, fetchUser);
}

export default mySagaUserDetail;
