import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

import LandingPage from "./LandingPage";
import Users from "./Users";
import Articles from "./Articles";
import Comments from "./Comments";
import UserInfo from "./userData";
import ArticleInfo from "./articleData";
import CommentInfo from "./commentData";

class App extends Component{
  render() {
    // if(users){
    //   list = <Users />;
    // } else if(articles){
    //   list = <Articles />;
    // }
    return(
      <Router>
        <div>
        <nav>
          <ul>
            <li><Link to={'/'} className="nav-link">Home</Link></li>
            <li><Link to={'/users'} className="nav-link">Users</Link></li>
            <li><Link to={'/articles'} className="nav-link">Articles</Link></li>
            <li><Link to={'/comments'} className="nav-link">Comments</Link></li>
          </ul>
        </nav>
          <Switch>
            <Route exact path='/' component={ LandingPage } />
            <Route exact path='/users' component={ Users } />
            <Route exact path='/articles' component={ Articles } />
            <Route exact path='/comments' component={ Comments } />
            <Route exact path='/user/:id' component={ UserInfo } />
            <Route exact path='/article/:id' component={ ArticleInfo } />
            <Route exact path='/comment/:id' component={ CommentInfo } />
          </Switch>
        </div>
      </Router>
    )
  }
}
export default App;
