// src/js/store/index.js
import { createStore, combineReducers, applyMiddleware } from "redux";
import createSagaMiddleware from "redux-saga";
import articles from "../reducers/articles";
import users from "../reducers/users";
import userInfo from "../reducers/userData";
import articleInfo from "../reducers/articleData";
import comments from "../reducers/comments";
// import mySagaArticle from "../sagas/articles";
// import mySagaUser from "../sagas/users";
// import mySagaComment from "../sagas/comments";
import mySagas from "../sagas/index";

const sagaMiddleware = createSagaMiddleware();

const store = createStore(
  combineReducers({
    users, articles, comments, userInfo,
  }),
  applyMiddleware(sagaMiddleware),
  // rootReducer
);

sagaMiddleware.run(mySagas);
// mySagas.registerWithMiddleware(sagaMiddleware);
export default store;
