const mapStateToProps = state => {
  return { user: state.userInfo };
};

const mapDispatchToProps = dispatch => {
  return {
    // deleteUser: user => dispatch(deleteUser(user)),
    fetchUser: () => dispatch(fetchUser()),
  };
};

class ConnectedList extends Component{

  constructor(){
