import { call, put, takeLatest } from 'redux-saga/effects';
import { FETCH_USERS, USERS_FETCH_SUCCESS, USERS_FETCH_ERROR } from '../constants/action-types-users';
// import { ENDPOINT } from '../constants/services';
// import { ENDPOINTUSER } from '../constants/user-services';
// import { ENDPOINTCOMMENT } from '../constants/comment-services';

function fetchAll(){
  return fetch('http://tdi-api.test:8080/api/user').then(response => response.json(), );
}

function* fetchUsers(){
  try{
    console.log('HEY HEY HEY');
    const users = yield call(fetchAll);
    yield put({type: USERS_FETCH_SUCCESS, payload: users});
    console.log('success try catch');
  } catch(e){
    console.log(e);
    yield put({type: USERS_FETCH_ERROR, message: e.message});
  }
}

/*ARTICLES*/
function fetchAll(){
  return fetch('http://tdi-api.test:8080/api/article').then(response => response.json(), );
}

function* fetchArticles(){
  try{
    console.log('HEY HEY HEY');
    const articles = yield call(fetchAll);
    yield put({type: ARTICLES_FETCH_SUCCESS, payload: articles});
    console.log('success try catch');
  } catch(e){
    console.log(e);
    yield put({type: ARTICLES_FETCH_ERROR, message: e.message});
  }
}

/*COMMENT*/
function fetchAll(){
  return fetch('http://tdi-api.test:8080/api/comment').then(response => response.json(), );
}

function* fetchComments(){
  try{
    console.log('HEY HEY HEY');
    const comments = yield call(fetchAll);
    yield put({type: COMMENTS_FETCH_SUCCESS, payload: comments});
    console.log('success try catch');
  } catch(e){
    console.log(e);
    yield put({type: COMMENTS_FETCH_ERROR, message: e.message});
  }
}


function* mySagaUser(){
  console.log('user saga init');
  yield takeLatest([
    takeLatest(FETCH_USERS, fetchUsers),
    takeLatest(FETCH_COMMENTS, fetchComments),
    takeLatest(FETCH_ARTICLES, fetchArticles),
  ]);
}

export default mySagaUser;
